const int[0, NUM_CONFIGURATIONS - 1] SELECTED_CONFIGURATION = 1;

/////////////////////////////////////////////////////////////////////////////////////////
//                                                                                     //
//     //\\     ||||        |||| |||||||||||| |||||||||||    ||||||||||||     //\\     //
//    //||\\    ||||        |||| |||||||||||| ||||     ||||  ||||||||||||    //||\\    //
